#include <stdio.h>
#include <string.h>

int main() {
  char buffer[10];
  char long_string[] = "This is a very long string that will overflow the buffer.";

  strcpy(buffer, long_string); // Vulnerable: strcpy doesn't check buffer size

  printf("Buffer: %s\n", buffer);

  return 0;
}
